import React, { Component } from 'react'
import API from '../services/API'
import Container from '../components/Container'
import Row from '../components/Row'
import Col from '../components/Col'

import Jumbotron from '../components/Jumbotron';
import Navbar from '../components/Navbar'
import CardWrapper from '../components/CardWrapper'
import Card from '../components/Card'


class Books extends Component {
    state = {
        result: [],
    }

    componentDidMount() {
        API
            .getBooks()
            .then(res => this.setState({result: res}))
            console.log(this.state.result);
    }

    getSavedBooks = () => {
        API
            .getBooks()
            .then(res => this.setState({result: res}))
            console.log(this.state.result);
    }

    render() {
        return (
            <div>
                <Jumbotron></Jumbotron>
                <Container>
                    <Row>
                        <Col>
                            <CardWrapper>
                            {this.state.result.map(result => (
                                    <Card
                                        key={result.id}
                                        url={result.image ? result.volumeInfo.imageLinks.smallThumbnail : "https://via.placeholder.com/128x124"}
                                        name={result.volumeInfo.title}
                                        author={result.volumeInfo.authors}
                                        infoLink={result.volumeInfo.infoLink}
                                        desc={result.volumeInfo.description ? result.volumeInfo.description : "No description"}
                                        handleBookSave={this.saveBook}
                                        id={result.id}
                                    />

                                ))}
                            </CardWrapper>
                        </Col>
                    </Row>

                </Container>
            </div>
        )
    }
}



export default Books
