import React, { Component } from 'react'
import API from '../services/API'

import Navbar from '../components/Navbar'
import Container from '../components/Container'
import Row from '../components/Row'
import Jumbotron from '../components/Jumbotron';
import Col from '../components/Col'
import Searchbar from '../components/Searchbar'
import Card from '../components/Card'
import CardWrapper from '../components/CardWrapper'



class Home extends Component {
    state = {
        result: [],
        dbSavedBooks: [],
        newArray: [],
        search: "",
        loading: false,

    };

    componentDidMount() {
        console.log('--component did mount')
        this.searchBooks("1984")
        this.getAllBooksNotInDb()

    }

    componentDidUpdate() {

        console.log('--component did update')
    }

    searchBooks = query => {
        this.setState({ loading: true })
        API
            .callGoogle(query)
            .then(res => {
                this.setState({ loading: false, result: res.data })
                console.log('result', res.data)
            })
            // .catch(err => console.log(err));
    }

    handleInputChange = e => {
        const value = e.target.value;
        // const name = e.target.name;
        this.setState({
            search: value
        });
    };

    // When the form is submitted, search the OMDB API for the value of `this.state.search`
    handleFormSubmit = e => {
        e.preventDefault();
        // run google call with search parameter 
        this.searchBooks(this.state.search);
        console.log(this.state.search)
        this.setState({
            search: ""
        })
    };



    saveBook = (e) => {
        const thisCardsId = e.target.getAttribute('data-id');
        const newSavedBook = this.state.result
        newSavedBook
            .filter(result => result.id === thisCardsId)
            .map(book => {
                API.saveBook({
                    bookId: book.id,
                    title: book.volumeInfo.title,
                    authors: book.volumeInfo.authors,
                    description: book.volumeInfo.description,
                    image: book.volumeInfo.imageLinks.smallThumbnail,
                    link: book.volumeInfo.infoLink,
                })
            })
    }




    getAllBooksNotInDb = () => {
        // create empty array to hold books in database ids
       const dbBooksIds = [];
    //    get all the books from the database 
        API.getBooks({})
        .then(res => {
            // set the state of the dbSavedBooks array with what we get back from the database 
            this.setState({dbSavedBooks: res.data})
            console.log('dbSavedBooks', res.data)
        });
        // iterate over the state and push each book id to dbBooksIds array
        this.state.dbSavedBooks.forEach(book =>{
            dbBooksIds.push(book.BookId)
        });
        // filter through all the books in the results state where all the google api books are store and get only books  
        // where id doesn't match the ids in the dbBooksIds array
       const notExcludedBooks= this.state.result.filter(book => dbBooksIds.includes(book.id));
    //    set the state 
       this.setState({newArray: notExcludedBooks})
       console.log('new results array', notExcludedBooks)

    }

    // saveBook = (e) => {
    //     const thisCardsId = e.target.getAttribute('data-id');
    //     const newSavedBook = this.state.result
    //       const allotherbooks =  newSavedBook.filter(result => result.id !== thisCardsId) 
    //                 this.setState({result: allotherbooks})
    //             // } else {
    //             //     newSavedBook.map(book => {
    //             //         API.saveBook({
    //             //             title: book.volumeInfo.title,
    //             //             authors: book.volumeInfo.authors,
    //             //             description: book.volumeInfo.description,
    //             //             image: book.volumeInfo.imageLinks.smallThumbnail,
    //             //             link: book.volumeInfo.infoLink,
    //             //         })
    //             //     })

    //             // }


    //         }






    render() {
        if (this.state.loading) {
            return (
                <div>
                    <Navbar />
                    <Jumbotron>
                        <Searchbar />
                    </Jumbotron>
                    <div className="row">
                        <div className="col l12 center align">
                            <div class="preloader-wrapper big active">
                                <div class="spinner-layer spinner-blue-only">
                                    <div class="circle-clipper left">
                                        <div class="circle"></div>
                                    </div><div class="gap-patch">
                                        <div class="circle"></div>
                                    </div><div class="circle-clipper right">
                                        <div class="circle"></div>
                                    </div>
                                </div>
                            </div>

                        </div>

                    </div>

                </div>
            )


        }
        // else 
        return (

            <div>
                <Navbar />
                <Jumbotron>
                    <Searchbar
                        value={this.state.search}
                        handleInputChange={this.handleInputChange}
                        handleFormSubmit={this.handleFormSubmit}
                    />

                </Jumbotron>

                <Container>
                    <Row>
                        <Col>


                            <CardWrapper title={'Results'}>



                                {this.state.newArray.map(result => (
                                    <Card
                                        key={result.id}
                                        url={result.volumeInfo.imageLinks ? result.volumeInfo.imageLinks.smallThumbnail : "https://via.placeholder.com/128x124"}
                                        name={result.volumeInfo.title}
                                        author={result.volumeInfo.authors}
                                        infoLink={result.volumeInfo.infoLink}
                                        desc={result.volumeInfo.description ? result.volumeInfo.description : "No description"}
                                        handleBookSave={this.saveBook}
                                        id={result.id}
                                        leftButton={"View"}
                                        rightButton={"Save"}
                                    />

                                ))}


                            </CardWrapper>



                        </Col>
                    </Row>
                </Container>
            </div>


        )
    }
}






export default Home
