import React from 'react'
import styled from 'styled-components'


const Div = styled.div`
    margin-top: 50px;
`

const textareaStyle = {
        borderBottom: '1 solid black',

}




const Searchbar = (props) => {
    return (
        <Div className='container'>
            <div className="row">
                <div className="input-field col l12 center-align">
                    <textarea id="textarea1" style={textareaStyle} className="materialize-textarea center-align"
                        onChange={props.handleInputChange}
                        value={props.value}
                        name="search"
                        type="text"
                        placeholder="Search For a Book"
                        id="search"
                    />
                    <a className="waves-effect waves-light btn" onClick={props.handleFormSubmit}>search</a>

                </div>
            </div>
        </Div>

















    )
}

export default Searchbar
